contracts/Presale.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@chainlink/contracts/src/v0.8/vrf/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/vrf/interfaces/VRFCoordinatorV2Interface.sol";

interface IVerifyzVerifier {
    function transfer(address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
    function decimals() external view returns (uint8);
}

interface IRewardsDistributor {
    function setLifetimeWinners(address[5] calldata winners) external;
}

/**
 * @title Presale
 * @dev Enhanced token presale for Verifyz Protocol with VRF winner selection
 * As per whitepaper: 35% of total supply (175M tokens) for presale
 * 5 random winners selected via Chainlink VRF for lifetime rewards
 */
contract Presale is Ownable, ReentrancyGuard, VRFConsumerBaseV2 {
    using SafeERC20 for IERC20;

    IVerifyzVerifier public token;
    IRewardsDistributor public rewardsDistributor;

    // Presale parameters
    uint256 public constant PRESALE_ALLOCATION = 175_000_000 * 10**18; // 35% of 500M
    uint256 public price; // price per token in wei
    uint256 public duration; // presale duration in seconds
    uint256 public presaleStartTime;
    uint256 public presaleEndTime;
    uint256 public totalTokensSold;
    uint256 public totalRaised;
    bool public hasStarted;
    bool public hasEnded;
    bool public winnersSelected;

    // Caps and limits
    uint256 public hardCap;
    uint256 public softCap;
    bool public softCapReached;
    bool public hardCapReached;

    // VRF for random winner selection
    VRFCoordinatorV2Interface internal COORDINATOR;
    bytes32 internal keyHash;
    uint64 internal subscriptionId;
    uint32 internal callbackGasLimit;
    uint16 internal requestConfirmations;
    uint32 internal numWords;
    uint256 public randomSeed;
    address[5] public winners;

    // Participant tracking
    address[] public participants;
    mapping(address => uint256) public contributions;
    mapping(address => bool) public hasContributed;
    mapping(address => bool) public tokensClaimed;

    // Vesting - 25% TGE, remainder over 6 months
    uint256 public constant TGE_PERCENTAGE = 25; // 25% at Token Generation Event
    uint256 public constant VESTING_DURATION = 180 days; // 6 months
    uint256 public tgeTime;
    mapping(address => uint256) public claimedTokens;

    event PresaleStarted(uint256 startTime, uint256 endTime);
    event PresaleEnded();
    event Contributed(address indexed contributor, uint256 amount, uint256 tokens);
    event WinnersRequested(bytes32 requestId);
    event WinnersSelected(address[5] winners);
    event TokensClaimed(address indexed claimer, uint256 amount);
    event Refund(address indexed contributor, uint256 amount);

    constructor(
        address tokenAddress,
        address rewardsDistributorAddress,
        address owner,
        uint256 _price,
        uint256 _duration,
        address _vrfCoordinator,
        uint64 _subscriptionId,
        bytes32 _keyHash,
        uint32 _callbackGasLimit,
        uint16 _requestConfirmations,
        uint32 _numWords
    )
        Ownable(owner)
        VRFConsumerBaseV2(_vrfCoordinator)
    {
        token = IVerifyzVerifier(tokenAddress);
        rewardsDistributor = IRewardsDistributor(rewardsDistributorAddress);
        price = _price;
        duration = _duration;

        // VRF v2 setup
        COORDINATOR = VRFCoordinatorV2Interface(_vrfCoordinator);
        subscriptionId = _subscriptionId;
        keyHash = _keyHash;
        callbackGasLimit = _callbackGasLimit;
        requestConfirmations = _requestConfirmations;
        numWords = _numWords;

        // Default caps (can be updated by owner)
        hardCap = 1000 * 10**18; // 1000 ETH
        softCap = 100 * 10**18;  // 100 ETH

        hasStarted = false;
        hasEnded = false;
        winnersSelected = false;
        totalTokensSold = 0;
        totalRaised = 0;
    }

    function startPresale() external onlyOwner {
        require(!hasStarted, "Presale already started");
        hasStarted = true;
        presaleStartTime = block.timestamp;
        presaleEndTime = block.timestamp + duration;
        emit PresaleStarted(presaleStartTime, presaleEndTime);
    }

    function contribute() external payable nonReentrant {
        require(hasStarted, "Presale not started");
        require(block.timestamp <= presaleEndTime, "Presale has ended");
        require(!hardCapReached, "Hard cap reached");
        require(msg.value > 0, "Must send ETH");

        uint256 newTotalRaised = totalRaised + msg.value;
        require(newTotalRaised <= hardCap, "Contribution exceeds hard cap");

        // Calculate tokens to be allocated
        uint256 tokenAmount = (msg.value * 1e18) / price;
        require(totalTokensSold + tokenAmount <= PRESALE_ALLOCATION, "Not enough tokens available");

        // Add to participants if first contribution
        if (!hasContributed[msg.sender]) {
            participants.push(msg.sender);
            hasContributed[msg.sender] = true;
        }

        contributions[msg.sender] += msg.value;
        totalTokensSold += tokenAmount;
        totalRaised = newTotalRaised;

        emit Contributed(msg.sender, msg.value, tokenAmount);

        // Check caps
        if (totalRaised >= hardCap) {
            hardCapReached = true;
            _endPresale();
        } else if (totalRaised >= softCap && !softCapReached) {
            softCapReached = true;
        }
    }

    function endPresale() external onlyOwner {
        require(hasStarted, "Presale not started");
        require(block.timestamp >= presaleEndTime || hardCapReached, "Cannot end presale yet");
        _endPresale();
    }

    function _endPresale() internal {
        if (!hasEnded) {
            hasEnded = true;
            hasStarted = false;
            emit PresaleEnded();

            // Set TGE time for vesting
            tgeTime = block.timestamp;

            // Request VRF for winner selection if we have enough participants
            if (participants.length >= 5 && softCapReached) {
                _requestWinners();
            }
        }
    }

    function _requestWinners() internal {
        uint256 requestId_ = COORDINATOR.requestRandomWords(
            keyHash,
            subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );
        emit WinnersRequested(bytes32(requestId_));
    }

    function fulfillRandomWords(uint256 /*requestId_*/, uint256[] memory randomWords) internal override {
        randomSeed = randomWords[0];
        _selectWinners(randomSeed);
    }

    function _selectWinners(uint256 seed) internal {
        require(participants.length >= 5, "Not enough participants");
        require(!winnersSelected, "Winners already selected");

        uint256 participantCount = participants.length;
        bool[5] memory selectedIndices;

        for (uint256 i = 0; i < 5; i++) {
            uint256 randomIndex;
            do {
                seed = uint256(keccak256(abi.encode(seed, i)));
                randomIndex = seed % participantCount;
            } while (selectedIndices[randomIndex]);

            selectedIndices[randomIndex] = true;
            winners[i] = participants[randomIndex];
        }

        winnersSelected = true;
        rewardsDistributor.setLifetimeWinners(winners);
        emit WinnersSelected(winners);
    }

    // Manual winner selection fallback (if VRF fails)
    function selectWinnersManually(uint256 seed) external onlyOwner {
        require(hasEnded, "Presale not ended");
        require(!winnersSelected, "Winners already selected");
        require(participants.length >= 5, "Not enough participants");
        _selectWinners(seed);
    }

    function claimTokens() external nonReentrant {
        require(hasEnded, "Presale not ended");
        require(softCapReached, "Soft cap not reached");
        require(contributions[msg.sender] > 0, "No contribution found");

        uint256 totalTokens = (contributions[msg.sender] * 1e18) / price;
        uint256 availableTokens = getClaimableTokens(msg.sender);

        require(availableTokens > 0, "No tokens available to claim");

        claimedTokens[msg.sender] += availableTokens;
        IERC20(address(token)).safeTransfer(msg.sender, availableTokens);

        emit TokensClaimed(msg.sender, availableTokens);
    }

    function getClaimableTokens(address contributor) public view returns (uint256) {
        if (!hasEnded || !softCapReached || contributions[contributor] == 0) {
            return 0;
        }

        uint256 totalTokens = (contributions[contributor] * 1e18) / price;
        uint256 tgeTokens = (totalTokens * TGE_PERCENTAGE) / 100;
        uint256 vestedTokens = 0;

        if (block.timestamp > tgeTime) {
            uint256 timeSinceTGE = block.timestamp - tgeTime;
            if (timeSinceTGE >= VESTING_DURATION) {
                vestedTokens = totalTokens - tgeTokens;
            } else {
                vestedTokens = ((totalTokens - tgeTokens) * timeSinceTGE) / VESTING_DURATION;
            }
        }

        uint256 totalAvailable = tgeTokens + vestedTokens;
        return totalAvailable - claimedTokens[contributor];
    }

    function getRefund() external nonReentrant {
        require(hasEnded, "Presale not ended");
        require(!softCapReached, "Soft cap was reached, no refunds");

        uint256 amount = contributions[msg.sender];
        require(amount > 0, "No contribution to refund");

        contributions[msg.sender] = 0;
        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "Refund failed");

        emit Refund(msg.sender, amount);
    }

    // Owner functions
    function setPrice(uint256 newPrice) external onlyOwner {
        require(!hasStarted, "Cannot change price after start");
        price = newPrice;
    }

    function setCaps(uint256 _softCap, uint256 _hardCap) external onlyOwner {
        require(!hasStarted, "Cannot change caps after start");
        require(_softCap <= _hardCap, "Soft cap must be <= hard cap");
        softCap = _softCap;
        hardCap = _hardCap;
    }

    function withdrawETH() external onlyOwner {
        require(hasEnded, "Presale not ended");
        require(softCapReached, "Soft cap not reached");

        uint256 balance = address(this).balance;
        require(balance > 0, "No ETH to withdraw");

        (bool success, ) = payable(owner()).call{value: balance}("");
        require(success, "Withdrawal failed");
    }

    function withdrawUnsoldTokens() external onlyOwner {
        require(hasEnded, "Presale not ended");

        uint256 unsoldTokens = PRESALE_ALLOCATION - totalTokensSold;
        if (unsoldTokens > 0) {
            IERC20(address(token)).safeTransfer(owner(), unsoldTokens);
        }
    }


    // View functions
    function getParticipantCount() external view returns (uint256) {
        return participants.length;
    }

    function getWinners() external view returns (address[5] memory) {
        return winners;
    }
}

contracts/Rewards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title Rewards
 * @dev General rewards distribution for community incentives and airdrops
 * Separate from lifetime rewards which are handled by RewardsDistributor
 */
contract Rewards is Ownable {
    using SafeERC20 for IERC20;
    IERC20 public rewardToken;

    event RewardDistributed(address indexed to, uint256 amount);
    event BatchRewardsDistributed(address[] recipients, uint256[] amounts);

    constructor(address tokenAddress) Ownable(msg.sender) {
        rewardToken = IERC20(tokenAddress);
    }

    function distributeReward(address to, uint256 amount) external onlyOwner {
        require(to != address(0), "Invalid address");
        require(amount > 0, "Invalid amount");

        rewardToken.safeTransfer(to, amount);

        emit RewardDistributed(to, amount);
    }

    function batchDistributeRewards(
        address[] calldata recipients,
        uint256[] calldata amounts
    ) external onlyOwner {
        require(recipients.length == amounts.length, "Arrays length mismatch");
        require(recipients.length > 0, "No recipients");

        for (uint256 i = 0; i < recipients.length; i++) {
            require(recipients[i] != address(0), "Invalid address");
            require(amounts[i] > 0, "Invalid amount");

            rewardToken.safeTransfer(recipients[i], amounts[i]);
        }

        emit BatchRewardsDistributed(recipients, amounts);
    }

    function withdrawTokens(uint256 amount) external onlyOwner {
        rewardToken.safeTransfer(owner(), amount);
    }

    function getBalance() external view returns (uint256) {
        return rewardToken.balanceOf(address(this));
    }
}

contracts/RewardsDistributor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title RewardsDistributor
 * @dev Distributes transaction fees to 5 lifetime reward winners
 * As per whitepaper: 1% of all transaction fees split equally among 5 winners
 */
contract RewardsDistributor is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    IERC20 public rewardToken;
    address public tokenContract; // Verifyz token allowed to call distributeFees
    address[5] public lifetimeWinners;
    bool public winnersSet;

    mapping(address => uint256) public pendingRewards;
    mapping(address => uint256) public claimedRewards;

    uint256 public totalDistributed;
    uint256 public totalClaimed;

    event RewardTokenSet(address indexed token);
event TokenContractSet(address indexed tokenContract);
event WinnersSet(address[5] winners);
    event FeesDistributed(uint256 amount);
    event RewardsClaimed(address indexed winner, uint256 amount);

    constructor() Ownable(msg.sender) {}



function setRewardToken(address _token) external onlyOwner {
    require(_token != address(0), "Invalid token");
    rewardToken = IERC20(_token);
    emit RewardTokenSet(_token);
}

function setTokenContract(address _tokenContract) external onlyOwner {
    require(_tokenContract != address(0), "Invalid address");
    tokenContract = _tokenContract;
    emit TokenContractSet(_tokenContract);
}
function setLifetimeWinners(address[5] calldata winners) external onlyOwner {
        require(!winnersSet, "Winners already set");

        for (uint256 i = 0; i < 5; i++) {
            require(winners[i] != address(0), "Invalid winner address");
            lifetimeWinners[i] = winners[i];
        }

        winnersSet = true;
        emit WinnersSet(winners);
    }

    function distributeFees(uint256 amount) external {
        require(msg.sender == tokenContract, "Unauthorized");
        require(winnersSet, "Winners not set yet");
        require(amount > 0, "Amount must be > 0");

        uint256 rewardPerWinner = amount / 5;
        if (rewardPerWinner == 0) return; // Too small to distribute

        for (uint256 i = 0; i < 5; i++) {
            pendingRewards[lifetimeWinners[i]] += rewardPerWinner;
        }

        totalDistributed += amount;
        emit FeesDistributed(amount);
    }

    function claimRewards() external nonReentrant {
        require(winnersSet, "Winners not set yet");
        require(_isWinner(msg.sender), "Not a lifetime winner");

        uint256 amount = pendingRewards[msg.sender];
        require(amount > 0, "No rewards to claim");

        pendingRewards[msg.sender] = 0;
        claimedRewards[msg.sender] += amount;
        totalClaimed += amount;

        rewardToken.safeTransfer(msg.sender, amount);

        emit RewardsClaimed(msg.sender, amount);
    }

    function _isWinner(address account) internal view returns (bool) {
        for (uint256 i = 0; i < 5; i++) {
            if (lifetimeWinners[i] == account) {
                return true;
            }
        }
        return false;
    }

    function getPendingRewards(address winner) external view returns (uint256) {
        return pendingRewards[winner];
    }

    function getClaimedRewards(address winner) external view returns (uint256) {
        return claimedRewards[winner];
    }

    function getWinners() external view returns (address[5] memory) {
        return lifetimeWinners;
    }}

contracts/Staking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title Staking
 * @dev Staking contract for Verifyz Protocol tokens
 * Allows users to stake VFZ tokens and earn rewards
 */
contract Staking is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    IERC20 public stakingToken;
    uint256 public rewardRate; // reward tokens per second per token staked
    uint256 public lastUpdateTime;
    uint256 public rewardPerTokenStored;
    uint256 public totalStaked;

    struct UserInfo {
        uint256 amount;
        uint256 rewardPerTokenPaid;
        uint256 rewards;
        uint256 lastStakeTime;
    }

    mapping(address => UserInfo) public userInfo;

    event Staked(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount);
    event RewardsClaimed(address indexed user, uint256 amount);
    event RewardRateUpdated(uint256 newRate);

    constructor(address tokenAddress, uint256 _rewardRate) Ownable(msg.sender) {
        stakingToken = IERC20(tokenAddress);
        rewardRate = _rewardRate;
        lastUpdateTime = block.timestamp;
    }

    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = block.timestamp;

        if (account != address(0)) {
            UserInfo storage user = userInfo[account];
            user.rewards = earned(account);
            user.rewardPerTokenPaid = rewardPerTokenStored;
        }
        _;
    }

    function rewardPerToken() public view returns (uint256) {
        if (totalStaked == 0) {
            return rewardPerTokenStored;
        }
        return rewardPerTokenStored + 
               (((block.timestamp - lastUpdateTime) * rewardRate * 1e18) / totalStaked);
    }

    function earned(address account) public view returns (uint256) {
        UserInfo memory user = userInfo[account];
        return ((user.amount * (rewardPerToken() - user.rewardPerTokenPaid)) / 1e18) + user.rewards;
    }

    function stake(uint256 amount) external nonReentrant updateReward(msg.sender) {
        require(amount > 0, "Amount must be > 0");
        stakingToken.safeTransferFrom(msg.sender, address(this), amount);

        UserInfo storage user = userInfo[msg.sender];
        user.amount += amount;
        user.lastStakeTime = block.timestamp;
        totalStaked += amount;

        emit Staked(msg.sender, amount);
    }

    function unstake(uint256 amount) external nonReentrant updateReward(msg.sender) {
        UserInfo storage user = userInfo[msg.sender];
        require(user.amount >= amount, "Insufficient staked amount");
        require(amount > 0, "Amount must be > 0");

        user.amount -= amount;
        totalStaked -= amount;

        stakingToken.safeTransfer(msg.sender, amount);

        emit Unstaked(msg.sender, amount);
    }

    function claimRewards() external nonReentrant updateReward(msg.sender) {
        UserInfo storage user = userInfo[msg.sender];
        uint256 reward = user.rewards;
        require(reward > 0, "No rewards to claim");

        user.rewards = 0;
        stakingToken.safeTransfer(msg.sender, reward);

        emit RewardsClaimed(msg.sender, reward);
    }

    function exit() external nonReentrant updateReward(msg.sender) {
        UserInfo storage user = userInfo[msg.sender];

        uint256 stakedAmount = user.amount;
        uint256 reward = user.rewards;

        if (stakedAmount > 0) {
            user.amount = 0;
            totalStaked -= stakedAmount;
            stakingToken.safeTransfer(msg.sender, stakedAmount);
            emit Unstaked(msg.sender, stakedAmount);
        }

        if (reward > 0) {
            user.rewards = 0;
            stakingToken.safeTransfer(msg.sender, reward);
            emit RewardsClaimed(msg.sender, reward);
        }
    }

    // Owner functions
    function setRewardRate(uint256 _rate) external onlyOwner updateReward(address(0)) {
        rewardRate = _rate;
        emit RewardRateUpdated(_rate);
    }

    function withdrawTokens(uint256 amount) external onlyOwner {
        stakingToken.safeTransfer(owner(), amount);
    }

    function addRewards(uint256 amount) external onlyOwner {
        stakingToken.safeTransferFrom(msg.sender, address(this), amount);
    }

    // View functions
    function getStakedAmount(address account) external view returns (uint256) {
        return userInfo[account].amount;
    }

    function getRewardBalance() external view returns (uint256) {
        return stakingToken.balanceOf(address(this)) - totalStaked;
    }
}

contracts/VerifyzVerifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

interface IRewardsDistributor {
    function distributeFees(uint256 amount) external;
}

/**
 * @title VerifyzVerifier (VFZ Token)
 * @dev ERC20 token with 5% transaction fees as per whitepaper
 * Total Supply: 500M tokens
 * Fee Structure: 5% per transaction
 * - 1% to 5 lifetime reward winners
 * - 4% to project treasury
 */
contract VerifyzVerifier is ERC20, Ownable, ReentrancyGuard {
    uint256 public constant TOTAL_SUPPLY = 500_000_000 * 10**18; // 500M tokens
    uint256 public constant FEE_PERCENTAGE = 5; // 5% transaction fee
    uint256 public constant REWARD_WINNERS_FEE = 1; // 1% to winners
    uint256 public constant TREASURY_FEE = 4; // 4% to treasury

    address public rewardsDistributor;
    address public treasury;
    bool public feesEnabled = false;

    // Exempted addresses (presale contract, etc.)
    mapping(address => bool) public feeExempt;

    event FeesEnabled(bool enabled);
    event RewardsDistributorSet(address indexed distributor);
    event TreasurySet(address indexed treasury);
    event FeeExemptionSet(address indexed account, bool exempt);

    constructor(
        string memory name_,
        string memory symbol_,
        address treasury_
    ) ERC20(name_, symbol_) Ownable(msg.sender) {
        treasury = treasury_;
        _mint(msg.sender, TOTAL_SUPPLY);

        // Owner is fee exempt by default
        feeExempt[msg.sender] = true;
    }

    function setRewardsDistributor(address _rewardsDistributor) external onlyOwner {
        require(_rewardsDistributor != address(0), "Invalid address");
        rewardsDistributor = _rewardsDistributor;
        feeExempt[_rewardsDistributor] = true;
        emit RewardsDistributorSet(_rewardsDistributor);
    }

    function setTreasury(address _treasury) external onlyOwner {
        require(_treasury != address(0), "Invalid address");
        treasury = _treasury;
        emit TreasurySet(_treasury);
    }

    function setFeesEnabled(bool _enabled) external onlyOwner {
        feesEnabled = _enabled;
        emit FeesEnabled(_enabled);
    }

    function setFeeExemption(address account, bool exempt) external onlyOwner {
        feeExempt[account] = exempt;
        emit FeeExemptionSet(account, exempt);
    }

    function _update(address from, address to, uint256 amount) internal override {
        // Handle fee logic only for transfers (not mints or burns)
        if (from != address(0) && to != address(0) && feesEnabled && !feeExempt[from] && !feeExempt[to]) {
            uint256 feeAmount = (amount * FEE_PERCENTAGE) / 100;
            uint256 transferAmount = amount - feeAmount;

            if (feeAmount > 0) {
                // 1% to rewards distributor for lifetime winners
                uint256 rewardsAmount = (amount * REWARD_WINNERS_FEE) / 100;
                // 4% to treasury
                uint256 treasuryAmount = feeAmount - rewardsAmount;

                if (rewardsDistributor != address(0) && rewardsAmount > 0) {
                    super._update(from, rewardsDistributor, rewardsAmount);
                    IRewardsDistributor(rewardsDistributor).distributeFees(rewardsAmount);
                } else {
                    // If distributor not set, send to treasury
                    treasuryAmount += rewardsAmount;
                }

                if (treasuryAmount > 0) {
                    super._update(from, treasury, treasuryAmount);
                }

                // Transfer the net amount to recipient
                super._update(from, to, transferAmount);
            } else {
                super._update(from, to, amount);
            }
        } else {
            // No fees for mints, burns, or exempted addresses
            super._update(from, to, amount);
        }
    }
}

